changeSet.sql:
CREATE TABLE IF NOT EXISTS public.animal(
id           bigint not null primary key,
breed         varchar(255),
name         varchar(255),
cost         numeric(38, 2),
character    varchar(255),
birth_date   date,
type_id      bigint
);

COMMENT ON COLUMN public.animal.id IS 'Уникальный ключ записи';
COMMENT ON COLUMN public.animal.breed IS 'Порода';
COMMENT ON COLUMN public.animal.name IS 'Имя';
COMMENT ON COLUMN public.animal.cost IS 'Цена';
COMMENT ON COLUMN public.animal.character IS 'Характер';
COMMENT ON COLUMN public.animal.birth_date IS 'Дата родждения';
COMMENT ON COLUMN public.animal.type_id IS 'Id типа животного';
